const component = ({ msg }) => {
    const texts = msg.split("\n").map((item, index) => {
      // <></> は key を設定できないので、<React.Fragment /> を使う
      return (
        <React.Fragment key={index}>{item}<br /></React.Fragment>
      );
    });
    return <div>{texts}</div>;
  }
